<?php
/**
 * Plugin Name: OnionPress Favicon
 * Description: Sets the OnionPress purple onion as the site favicon.
 */

$onionpress_favicon = '';

add_action('wp_head', function () use ($onionpress_favicon) {
    echo '<link rel="icon" type="image/png" href="data:image/png;base64,' . $onionpress_favicon . '">' . "\n";
}, 1);

add_action('login_head', function () use ($onionpress_favicon) {
    echo '<link rel="icon" type="image/png" href="data:image/png;base64,' . $onionpress_favicon . '">' . "\n";
}, 1);
